con:config.script:
import org.apache.commons.codec.binary.Base64;
import groovy.json.JsonSlurper;

//Get Content From Previous step'
def response = testRunner.testCase.getTestStepAt(0).getPropertyValue("response");//or getTestStepByName("PCA-Request1")
def responseJson = new JsonSlurper().parseText(response);
String fileName = responseJson.Filename;
String filecontent = responseJson.Content;
log.info("File " + fileName + "is retrieved Successfully");
// Decode base64
def b64 = new Base64();
byte[] textBytes = b64.decode(filecontent.getBytes());

// Write & close output file
def groovyUtils = new  com.eviware.soapui.support.GroovyUtils(context)
String projectPath = groovyUtils.projectPath
FileOutputStream stream = new java.io.FileOutputStream(new File(projectPath+"/ComplianceAssessmentsOutputFiles/"+fileName));
stream.write(textBytes);
stream.flush(); 
stream.close();

